mapper.insert:
INSERT INTO
device(

)
VALUES (#{id},#{typeId},#{projectId},#{mongoId},#{address},#{status},#{name},#{number},#{price},#{producer},#{produceTime},#{createTime},#{priceUnit},#{lng},#{lat},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.update:
update device
typeId=#{typeId}, projectId=#{projectId}, mongoId=#{mongoId}, address=#{address}, status=#{status}, name=#{name}, number=#{number}, price=#{price}, producer=#{producer}, produceTime=#{produceTime}, priceUnit=#{priceUnit}, lng=#{lng}, lat=#{lat},
where id=#{id} and appId=#{appId}

mapper.select:
select id
from device
where NUMBER=#{number} and APPID=#{appId}

select device.*,device_type.name as deviceType,p.name project
from device
LEFT JOIN device_type ON device.typeId=device_type.id
LEFT JOIN project p ON device.projectId=p.id
where
 device.APPID=#{appId}
 AND device.typeId=#{typeId}
 AND device.optionType=#{optionType}
AND device.number like CONCAT('%',#{number},'%') 
AND device.name like CONCAT('%',#{name},'%')
AND device.producer like CONCAT('%',#{producer},'%')
 AND device.produceTime>=#{startTime}
 AND device.produceTime<=#{endTime}  
 AND device.projectId=#{projectId}
