mapper.insert:
INSERT INTO
device(

)
VALUES (#{id}, #{mainDeviceId},#{batchId},#{supplierId},#{typeId},#{storehouseId},#{contractId},#{address},#{status},#{optionType},#{name},#{number},#{price},#{produceTime},#{createTime},#{priceUnit},#{lng},#{lat},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.update:
update device
BATCHID=#{batchId}, SUPPLIERID=#{supplierId}, OPTIONTYPE=#{optionType}, TYPEID=#{typeId}, STOREHOUSEID=#{storehouseId}, CONTRACTID=#{contractId}, ADDRESS=#{address}, STATUS=#{status}, NAME=#{name}, NUMBER=#{number}, PRICE=#{price}, PRODUCETIME=#{produceTime}, PRICEUNIT=#{priceUnit}, LNG=#{lng}, LAT=#{lat}, APPID=#{appId}
where ID=#{id};

mapper.select:
select id
from device
where NUMBER=#{number} and APPID=#{appId}

select device.id,device.name,device.number,device.optionType,device_type.name as deviceType,storehouse.name as storehouseName,contract.name as contractName,device.price,device.createTime,supplier.name as supplierName,
device.produceTime,device.price,device.priceUnit,device.status,device.optionType,batch.number as batchNumber,device.contractId,device.batchId,device.storehouseId,device.typeId,device.supplierId,supplier.name as supplierName,customer.name as customerName
from device
LEFT JOIN device_type ON device.typeId=device_type.id
LEFT JOIN storehouse ON device.storehouseId=storehouse.id
LEFT JOIN contract ON device.contractId=contract.id
LEFT JOIN batch ON device.batchId=batch.id
LEFT JOIN supplier ON device.supplierId=supplier.id
LEFT JOIN customer ON contract.customerId=customer.id
where
 device.APPID=#{appId}
 AND device.batchId=#{batchId}
 AND device.typeId=#{typeId}
AND device_type.name like CONCAT('%',#{deviceType},'%')
 AND device.optionType=#{optionType}
 AND device.storehouseId=#{storehouseId}
 AND device_type.mainDevice=#{mainDevice}
AND device.number like CONCAT('%',#{number},'%') 
AND device.name like CONCAT('%',#{name},'%')
 AND device.produceTime>=#{startTime}
 AND device.produceTime<=#{endTime}

mapper.update:
update device set mainDeviceId=#{mainDeviceId},optionType=4
where device.appId=#{appId} and  ID in

    #{item}

update device set mainDeviceId=0,optionType=5
where device.appId=#{appId} and ID in

    #{item}

update device set optionType=3
where device.appId=#{appId} and ID in

    #{item}

update device set optionType=1
where device.appId=#{appId} and ID in

    #{item}

update device set optionType=2
where device.appId=#{appId} and ID in

    #{item}

mapper.select:
select device.id,device.name,device.optionType,device.number,device.address,device_type.name as deviceType,storehouse.name as storehouse,contract.name as contract,device.price,device.createTime,supplier.name as supplierName, device.produceTime,device.price,device.priceUnit,device.status,device.optionType,batch.number as batchNumber,device.contractId,device.batchId,device.storehouseId,device.typeId,device.supplierId,customer.name as customerName,contract.startTime,contract.endTime from device LEFT JOIN device_type ON device.typeId=device_type.id LEFT JOIN storehouse ON device.storehouseId=storehouse.id LEFT JOIN contract ON device.contractId=contract.id LEFT JOIN batch ON device.batchId=batch.id LEFT JOIN supplier ON device.supplierId=supplier.id LEFT JOIN customer ON contract.customerId=customer.id
device.mainDeviceId=#{mainDeviceId} AND device.APPID=#{appId}
order by device.typeId
