mapper.insert:
INSERT INTO
stock_out(

)
VALUES (#{id}, #{number},#{contractId},#{driver},#{carNumber},#{handler},#{transportId},#{storehouseId},#{description},#{createTime},#{image},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.select:
select stock_out.*,contract.name as contractName,customer.name as customerName,storehouse.name as storehouseName from stock_out LEFT JOIN contract ON stock_out.contractId=contract.id LEFT JOIN storehouse ON stock_out.storehouseId=storehouse.id LEFT JOIN customer ON contract.customerId=customer.id
stock_out.APPID=#{appId} AND stock_out.handler like CONCAT('%',#{user},'%') AND contract.name like CONCAT('%',#{device},'%') AND stock_out.createTime>=#{startTime} AND stock_out.createTime<=#{endTime}
order by stock_out.id desc

select stock_out.*,contract.name as contractName,customer.name as customerName,storehouse.name as storehouseName from stock_out LEFT JOIN contract ON stock_out.contractId=contract.id LEFT JOIN storehouse ON stock_out.storehouseId=storehouse.id LEFT JOIN customer ON contract.customerId=customer.id
stock_out.APPID=#{appId} AND stock_out.id=#{stockOutId}
order by stock_out.id desc

select stock_out.*,contract.name as contractName,customer.name as customerName,storehouse.name as storehouseName from stock_out LEFT JOIN contract ON stock_out.contractId=contract.id LEFT JOIN storehouse ON stock_out.storehouseId=storehouse.id LEFT JOIN customer ON contract.customerId=customer.id
stock_out.appId=#{appId} AND stock_out.contractId=#{contractId}
order by stock_out.id desc
