mapper.insert:
INSERT INTO
`trackrecord`(

)
VALUES (#{id},#{deviceId},#{startTime},#{endTime},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.update:
update `trackrecord`

    DEVICEID=#{deviceId},
    STARTTIME=#{startTime},
    ENDTIME=#{endTime},
    APPID=#{appId}

where id=#{id} and appId=#{appId}

mapper.select:
SELECT tr.*,d.name as deviceName,d.number as deviceNumber,ug.id as ugId,g.name as groupName,u.name as userName
FROM `trackrecord` as tr,device as d,`group` as g,user as u,user_group as ug
WHERE tr.appId=#{appId} AND d.id=#{deviceId} AND d.id = tr.deviceId AND tr.deviceId=ug.deviceId AND ug.groupId=g.id AND u.id=ug.userId
 AND tr.startTime>=#{sTime}
 AND tr.endTime<=#{eTime}
